INSERT INTO `wpassoc_posts` VALUES (1,1,'2017-11-04 22:26:30','2017-11-04 21:26:30','Excogitatum est super his, ut homines quidam ignoti, vilitate ipsa parum cavendi ad colligendos rumores per Antiochiae latera cuncta destinarentur relaturi quae audirent. hi peragranter et dissimulanter honoratorum circulis adsistendo pervadendoque divites domus egentium habitu quicquid noscere poterant vel audire latenter intromissi per posticas in regiam nuntiabant, id observantes conspiratione concordi, ut fingerent quaedam et cognita duplicarent in peius, laudes vero supprimerent Caesaris, quas invitis conpluribus formido malorum inpendentium exprimebat.\r\n\r\nVbi curarum abiectis ponderibus aliis tamquam nodum et codicem difficillimum Caesarem convellere nisu valido cogitabat, eique deliberanti cum proximis clandestinis conloquiis et nocturnis qua vi, quibusve commentis id fieret, antequam effundendis rebus pertinacius incumberet confidentia, acciri mollioribus scriptis per simulationem tractatus publici nimis urgentis eundem placuerat Gallum, ut auxilio destitutus sine ullo interiret obstaculo.','Actualité 2','','publish','open','open','','bonjour-tout-le-monde','','','2017-11-04 23:25:42','2017-11-04 22:25:42','',0,'http://association.yannchapuis.fr/?p=1',0,'post','',0),(2,1,'2017-11-04 22:26:30','2017-11-04 21:26:30','Page d\'accueil','Accueil','','publish','closed','open','','page-d-exemple','','','2017-11-04 22:50:50','2017-11-04 21:50:50','',0,'http://association.yannchapuis.fr/?page_id=2',0,'page','',0),(4,1,'2017-11-04 22:28:07','2017-11-04 21:28:07','','Page d’exemple','','inherit','closed','closed','','2-revision-v1','','','2017-11-04 22:28:07','2017-11-04 21:28:07','',2,'http://association.yannchapuis.fr/2017/11/04/2-revision-v1/',0,'revision','',0),(5,1,'2017-11-04 22:28:18','2017-11-04 21:28:18','','Accueil','','inherit','closed','closed','','2-revision-v1','','','2017-11-04 22:28:18','2017-11-04 21:28:18','',2,'http://association.yannchapuis.fr/2017/11/04/2-revis